import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav, button } from 'reactstrap'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textValue : ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state.textValue)
  }

  handleChange = (event) => {
    this.setState({textValue: event.target.value});
  }

  render() {
    return (
      <div className="App" style={{
        backgroundImage: `url(require("img/camo.jpg"))`,
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        backgroundRepeat: 'no-repeat',
      }}>
        <header className="App-header">
          <h1 className="text-center" id="title">
            MiliMili
          </h1>
        </header>

        <div className="navBar mt-5">
          <Navbar>
            <div className="mx-auto">
              <Nav>
                <NavItem><a href="#1" className="color=black">나의 일기</a>
                </NavItem>
                <NavItem className="ml-5"><a href="#2">남의 일기</a></NavItem>
              </Nav>
            </div>
          </Navbar>
        </div>
        <form className="myForm mt-5" onSubmit={this.handleSubmit}>
          <div className="diary-control">
            <div className="text-center">
              <div className="form">
                <textarea type="text" rows="20" cols="70" placeholder="오늘하루는 어떠셨나요?" onChange={this.handleChange}></textarea>
              </div>
            </div>
          </div>
          <div className="text-center">
            <button type="submit" className="btn btn-dark btn-lg mt-3 mb-3">
              제출
          </button>
          </div>
        </form>
      </div>

    );
  }
}

export default App;
