import React, { Component } from 'react'; //import React Component

import { DropdownButton, MenuItem, ButtonToolbar } from 'react-bootstrap';


class SignUpForm extends Component {
  constructor(props){
    super(props);
    this.state = {
      'email': undefined,
      'password': undefined,
      // anonymous: false,
      handle: undefined
    };
  }

  //update state for specific field
  handleChange(event) {
    let field = event.target.name; 
    let value = event.target.value; 

    let changes = {}; 
    changes[field] = value; 
    this.setState(changes); 
  }

  // signUp button
  handleSignUp(event) {
    event.preventDefault();
    this.props.signUpCallback(this.state.email, this.state.password);
  }

  //handle signIn button
  handleSignIn(event) {
    event.preventDefault(); 
    this.props.signInCallback(this.state.email, this.state.password);
  }

  handleType(event) {
    console.log(event.eventKey)
  }

  // handleAnonymous(event) {
  //   event.preventDefault();
  //   this.setState({anonymous: true})
  //   this.props.handleAnonymous(this.state.anonymous)
  // }

  render() {
    return (
      <form>
        {/* email field */}
        <div className="form-group">
          <label htmlFor="email">아이디</label>
          <input className="form-control" placeholder="이메일형식" id="email" type="email" name="email" onChange={(e) => this.handleChange(e)}/>
        </div>

        {/* password field*/}
        <div className="form-group">
          <label htmlFor="password">비밀번호</label>
          <input className="form-control" id="password" type="password" name="password"onChange={(e) => this.handleChange(e)}/>
        </div>

        {/* sign up and sign in */}
        <div className="form-group text-right">
          <button id="signupButton1"  className="btn" onClick={(e) => this.handleSignIn(e)}>로그인</button>
          <button id="signupButton" className="btn mr-2" onClick={(e) => this.handleSignUp(e)}>회원가입</button>

    <DropdownButton title="신분" id="dropdown-size-medium">
      <MenuItem eventKey="1" >미필</MenuItem>
      <MenuItem eventKey="2" >현역</MenuItem>
      <MenuItem eventKey="3" >전역</MenuItem>
      <MenuItem eventKey="4">others</MenuItem>
    </DropdownButton>

          {/* <button className = "btn" onClick={(e) => this.handleAnonymous(e)}>비회원</button> */}
        </div>
      </form>
    )
  }
}

export default SignUpForm
