  }

  componentDidMount() {
    firebase.database().ref(`shared/diaries/`).child(this.props.diary.id).child("comments").on("value", (snapshot) => {
      console.log(snapshot.val())
      if (snapshot.exists()) {
        this.setState({ comments: snapshot.val() })
      }
    })
  }

