import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav } from 'reactstrap'
import { HashRouter, Route, Switch, Link, Redirect } from 'react-router-dom';
import { TagCloud } from "react-tagcloud";



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      diaries: [],
      clickedDiary: ''
    }
  }
  textUpdate = (diary, date, title) => {
    let copiedDiaries = this.state.diaries;
    copiedDiaries.push({ diary, date, title });
    this.setState({ diaries: copiedDiaries });
  }

  clickUpdate = (diary) => {
    console.log(diary.value)
    this.setState({ clickedDiary: diary })
    console.log(this.state.clickedDiary.value)
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="text-center" id="title"><Link to="/" id="titleLink">
            MiliMili
            </Link>
          </h1>
        </header>

        <main className="navBar mt-5">
          <HashRouter>
            <div>
              <Navbar>
                <div className="mx-auto">
                  <Nav>
                    <NavItem><Link to={'/'}>일기쓰기</Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/SavedDiaries'}>나의 일기 </Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/others'}>남의 일기</Link></NavItem>
                  </Nav>
                </div>
              </Navbar>
              <Switch>
                <Route exact path='/' render={() => <WriteDiary textUpdate={this.textUpdate} />} />
                <Route path='/SavedDiaries' render={() => <SavedDiaries diaries={this.state.diaries} clickUpdate={this.clickUpdate} />} />
                <Route path='/ChosenDiary/:keySeconds' render={() => <ChosenDiary diary={this.state.clickedDiary} />} />
                <Redirect to='/' />
              </Switch>
            </div>
          </HashRouter>
        </main>
      </div>

    );
  }
}

class WriteDiary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textValue: '',
      title: ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleSubmit = (event) => {
    event.preventDefault();
    let date = new Date();
    this.props.textUpdate(this.state.textValue, date.getMilliseconds(), this.state.title);
  }
  handleChange = (event) => {
    this.setState({ textValue: event.target.value });
  }

  titleChange = (event) => {
    this.setState({ title: event.target.value })
  }

  render() {
    return (
      <div className="container text-center">
        <form className="myForm mt-5" onSubmit={this.handleSubmit}>
          <input className="mb-3 p-2" size="71" id="title" type="text" placeholder="제목" onChange={this.titleChange}></input>
          <div className="form">
            <textarea className="p-2" id="textarea" type="text" rows="20" cols="70" placeholder="오늘하루는 어떠셨나요?" onChange={this.handleChange}></textarea>
          </div>
          <button type="submit" className="btn btn-warning btn-lg mt-3 mb-3">
            제출
          </button>
        </form>
      </div>);
  }
}

class SavedDiaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      clickedDiary: ''
    }
  }

  handleClick(tag) {
    console.log(tag.value)
    this.props.clickUpdate(tag);
    this.setState({ redirect: true, clickedDiary: tag })
  }

  render() {
    let data = this.props.diaries.map((diary) => {
      // return <OneSavedDiary key={diary.date} oneDiary={diary} />
      return ({ value: diary.title, count: Math.random() * 50, diary: diary.diary, key: diary.date });
    })

    if (this.state.redirect) {
      // return <Redirect push to={'/ChosenDiary/' + this.state.clickedDiary.key}/>
      return <Redirect push to={{
        pathname: '/ChosenDiary/' + this.state.clickedDiary.key
      }} />

    }

    return (
      <div>
        < div className="container" id="wordCloud">
          <TagCloud className="text-center" id="tagCloud" minSize={50}
            maxSize={90}
            tags={data}
            onClick={(tag) => this.handleClick(tag)} />
        </div >
      </div >
    );
  }
}

class ChosenDiary extends Component {
  render() {
    return (
      <div>
        <div className="container text-center">
          <div className="jumbotron" style={{backgroundColor: 'white'}}>
            <h1 className="display-4">{this.props.diary.value}</h1>
            <p className="lead">
              {this.props.diary.diary}
            </p>
            <p className="lead">
            </p>

          </div>
          <button type="button" className="btn-sm btn-warning">
              공유하기
            </button>
            <button type="button" className="btn-sm btn-danger m-3">
              삭제하기
            </button>
        </div>
      </div>
    );
  }
}

export default App;
