import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav, Button, Modal, Popover } from 'reactstrap'
import { HashRouter, Route, Switch, Link } from 'react-router-dom';
import { TagCloud } from "react-tagcloud";



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      diaries: []
    }
  }
  textUpdate = (diary, date, title) => {
    let copiedDiaries = this.state.diaries;
    copiedDiaries.push({ diary, date, title });
    this.setState({ diaries: copiedDiaries });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="text-center" id="title">
            MiliMili
          </h1>
        </header>

        <main className="navBar mt-5">
          <HashRouter>
            <div>
              <Navbar>
                <div className="mx-auto">
                  <Nav>
                    <NavItem><Link to={'/'}>일기쓰기</Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/SavedDiaries'}>나의 일기 </Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/others'}>남의 일기</Link></NavItem>
                  </Nav>
                </div>
              </Navbar>
              <Switch>
                <Route exact path='/' render={() => <WriteDiary textUpdate={this.textUpdate} />} />
                <Route exact path='/SavedDiaries' render={() => <SavedDiaries diaries={this.state.diaries} />} />
              </Switch>
            </div>
          </HashRouter>
        </main>
      </div>

    );
  }
}

class WriteDiary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textValue: '',
      title: ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleSubmit = (event) => {
    event.preventDefault();
    let date = new Date();
    this.props.textUpdate(this.state.textValue, date.toLocaleString(), this.state.title);
  }
  handleChange = (event) => {
    this.setState({ textValue: event.target.value });
  }

  titleChange = (event) => {
    this.setState({ title: event.target.value })
  }

  render() {
    return (
      <div className="container text-center">
        <form className="myForm mt-5" onSubmit={this.handleSubmit}>
          <input className="mb-3 p-2" size="71" id="title" type="text" placeholder="제목" onChange={this.titleChange}></input>
          <div className="form">
            <textarea className="p-2" id="textarea" type="text" rows="20" cols="70" placeholder="오늘하루는 어떠셨나요?" onChange={this.handleChange}></textarea>
          </div>
          <button type="submit" className="btn btn-warning btn-lg mt-3 mb-3">
            제출
          </button>
        </form>
      </div>);
  }
}

class SavedDiaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }

  render() {
    let data = this.props.diaries.map((diary) => {
      // return <OneSavedDiary key={diary.date} oneDiary={diary} />
      return ({ value: diary.title, count: Math.random() * 50, diary: diary.diary, date: diary.date });
    })

    return (
      <div>
        {/* <ul>
          {diaryLists}
        </ul> */}
        <div className="container">
          <TagCloud className="text-center" id="tagCloud" minSize={50}
            maxSize={90}
            tags={data}
            onClick={tag => <popModal diary={tag} />} />
        </div>
      </div>
    );
  }
}

class PopModal extends Component {
  constructor(props, context) {
    super(props, context);

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);

    this.state = {
      show: false
    };
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  render() {
    const popover = (
      <Popover id="modal-popover" title="popover">
        very popover. such engagement
      </Popover>
    );
    // const tooltip = <Tooltip id="modal-tooltip">wow.</Tooltip>;

    return (
      <div>
        <p>Click to get the full Modal experience!</p>

        <Button bsStyle="primary" bsSize="large" onClick={this.handleShow}>
          Launch demo modal
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>Text in a modal</h4>
            <p>
              Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </p>

            <h4>Popover in a modal</h4>
            {/* <p>
              there is a{' '}
              <OverlayTrigger overlay={popover}>
                <a href="#popover">popover</a>
              </OverlayTrigger>{' '}
              here
            </p> */}

            <h4>Tooltips in a modal</h4>
            <p>
              there is a{' '}
              {/* <OverlayTrigger overlay={tooltip}>
                <a href="#tooltip">tooltip</a>
              </OverlayTrigger>{' '} */}
              here
            </p>

            <hr />

            <h4>Overflowing text to show scroll behavior</h4>
            <p>
              Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
              dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta
              ac consectetur ac, vestibulum at eros.
            </p>
            <p>
              Praesent commodo cursus magna, vel scelerisque nisl consectetur
              et. Vivamus sagittis lacus vel augue laoreet rutrum faucibus dolor
              auctor.
            </p>
            <p>
              Aenean lacinia bibendum nulla sed consectetur. Praesent commodo
              cursus magna, vel scelerisque nisl consectetur et. Donec sed odio
              dui. Donec ullamcorper nulla non metus auctor fringilla.
            </p>
