import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav } from 'reactstrap'
import { HashRouter, Route, Switch, Link, Redirect } from 'react-router-dom';
import { TagCloud } from "react-tagcloud";
import _ from 'lodash';
import firebase from 'firebase/app';
import 'firebase/database';
import SignUpForm from './SignUpForm';
import ChosenDiary from './ChosenDiary'
import SavedDiaries from './SavedDiaries'
import WriteDiary from './WriteDiary'
// import { forEach } from '@firebase/util';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      diaries: [],
      clickedDiary: '',
      clickedLogIn: false,
    }
  }
  textUpdate = (diary, date, title) => {
    let copiedDiaries = this.state.diaries;
   console.log(this.state.user)
    let currentUser = this.state.user;
    let newData = {
      userID: currentUser.uid,
      time: firebase.database.ServerValue.TIMESTAMP, diary: diary,
      title: title, date: date
    }
    copiedDiaries.push(newData);
    firebase.database().ref(`test/${currentUser.uid}/diaries`).child(date).set(newData)
    // console.log(firebase.database().ref(`text/${currentUser.uid}/diaries`).child(date))
    this.setState({ diaries: copiedDiaries });
  }

  shareDiary = (diary) => {
    console.log(diary)
    let currentUser = this.state.user;
    let data = {
      userID: currentUser.uid,
      time: firebase.database.ServerValue.TIMESTAMP, diary: diary.diary,
      title: diary.value, date: diary.key
    }
    let shared = false
    firebase.database().ref('shared/diaries').on("value", (snapshot) => {
      for (let key in snapshot.val()) {
        if(snapshot.val()[key].date === diary.key && snapshot.val()[key].userID === diary.userID){
          shared = true;
        }
      }
    })

    if(shared === false){
    firebase.database().ref(`shared/diaries/`).child(diary.userID +'-'+diary.key).set(data)
    }else{
      alert("이미 공유하신 게시물입니다!")
    }
  }

  removeMySharedDiary = (userID, date) => {
    console.log("sharedremove:")
    console.log(userID)
    console.log(date)
    firebase.database().ref(`shared/diaries`).on("value", (snapshot) => {
      for(let key in snapshot.val()){
        if(snapshot.val()[key].date === date && snapshot.val()[key].userID === userID){
          console.log("key:")
          console.log(key);
          firebase.database().ref(`shared/diaries`).child(key).remove()
        } 
      }
    })
  }

  removeDiary = (keySecond) => {
    let copiedDiaries = this.state.diaries;
    _.remove(copiedDiaries, (diary) => {
      return diary.date === keySecond
    })
    firebase.database().ref(`test/${this.state.user.uid}/diaries`).child(keySecond).remove();

    // console.log(copiedDiaries)
    this.setState({ diaries: copiedDiaries })
  }

  clickUpdate = (diary) => {
    // console.log(diary.value)
    this.setState({ clickedDiary: diary})
    // console.log(this.state.clickedDiary.value)
  }
  clickLogIn = () => {
    this.setState({ clickedLogIn: true })
  }

  componentDidMount() {
    this.authUnRegFunc = firebase.auth().onAuthStateChanged((firebaseUser) => {
      if (firebaseUser) {
        this.setState({
          user: firebaseUser,
        })
        firebase.database().ref(`test/${this.state.user.uid}/diaries`).on("value", (snapshot) => {
          // console.log(snapshot.val())
          let newArray = [];
          for (let key in snapshot.val()) {
            newArray.push(snapshot.val()[key])
          }
          console.log("snapshot:")
          console.log(newArray)
          this.setState({ diaries: newArray })
        })
      } else {
        this.setState({ user: null })
      }
    })
  }

  componentWillUnmount() {
    this.authUnRegFunc();
  }

  handleSignUp(email, password, handle, avatar) {
    this.setState({ errorMessage: null }); //clear any old errors
    firebase.auth().createUserWithEmailAndPassword(email, password)
      .then((user) => {
        user.updateProfile({
          displayName: handle,
          photoURL: avatar
        }).then((profile) => {
          return profile;
        }).catch((error) => {
          this.setState({ errorMessage: error.message })
          // console.log(this.state.errorMessage)
          // Alert your error to your user 
        })
      }).catch((error) => {
        // Alert your error to your user
        this.setState({ errorMessage: error.message })
      });

    /* TODO: sign up user here */
  }

  handleSignIn(email, password) {
    // window.location.reload();
    this.setState({ errorMessage: null });
    firebase.auth().signInWithEmailAndPassword(email, password)
      .catch(error => this.setState({ errorMessage: error.message })
      );
  }

  //A callback function for logging out the current user
  handleSignOut() {
    this.setState({ errorMessage: null }); //clear any old errors
    window.location = '/';
    firebase.auth().signOut()
      .catch(error => {
        this.setState({ errorMessage: error.message })
      })
    /* TODO: sign out user here */
  }


  render() {
    let content = null; //content to render
    if (!this.state.user) { //if logged out, show signup form
      content = (
        <div className="container mt-5">
        <h1 style={{ textAlign: 'center', color: '#6F6B29'}}>MiliMili</h1>
          <div className="row mt-5">
            <div className="col-sm-12 col-md-6 offset-md-3 shadow-sm mt-4" id="signupBox">
              {/* <img src="img/fruit.png" alt="brand logo" className="brandLogoBig mx-auto d-block" style={{ width: 50, height: 50 }} /> */}
              <br />
              <SignUpForm
                signUpCallback={(email, password) => this.handleSignUp(email, password)}
                signInCallback={(email, password) => this.handleSignIn(email, password)}
              />
            </div>
          </div>
        </div>
      );
    } else {
      content = (
        <div>
          <header className="App-header">
            <h1 className="text-center" id="title"><Link to="/" id="titleLink">
              MiliMili
            </Link>
            </h1>
            <div className="text-center">
              {/* <button className="btn ml" onClick={this.clickLogIn}>
                  로그인
            </button> */}
              {this.state.user && <button className="btn" onClick={() => this.handleSignOut()}>
                로그아웃
            </button>}
            </div>
          </header>

          <main className="navBar mt-3">
            <HashRouter>
              <div>
                <Navbar>
                  <div className="mx-auto">
                    <Nav>
                      <NavItem><Link to={'/'}>일기쓰기</Link>
                      </NavItem>
                      <NavItem className="ml-5"><Link to={'/SavedDiaries'}>나의 일기 </Link>
                      </NavItem>
                      <NavItem className="ml-5"><Link to={'/others'}>공유된 일기</Link></NavItem>
                    </Nav>
                  </div>
                </Navbar>
                <Switch>
                  <Route exact path='/' render={() => <WriteDiary textUpdate={this.textUpdate} />} />
                  {console.log("state diaries")}

                  {console.log(this.state.diaries)}
                  {/* <IndexRoute render={() => <SavedDiaries diaries={this.state.diaries} clickUpdate={this.clickUpdate} />}/> */}
                  <Route path='/SavedDiaries' render={() => <SavedDiaries diaries={this.state.diaries} clickUpdate={this.clickUpdate} />} />
                  <Route path='/others' render={() => <OthersDiaries clickUpdate={this.clickUpdate}/>} />
                  <Route path='/ChosenDiary/:keySeconds' render={() => <ChosenDiary diary={this.state.clickedDiary} currentUser={this.state.user}
                    removeDiary={this.removeDiary} shareDiary={this.shareDiary} removeMySharedDiary={this.removeMySharedDiary}/>} />
                  <Redirect to='/' />
                </Switch>
              </div>
            </HashRouter>
          </main>
        </div>
      );

    }
    return (
      <div className="App">
        {content}
      </div>

    );
  }
}

class OthersDiaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      clickedDiary: '',
    }
  }

  handleClick(tag) {
    console.log(tag.value)
    this.props.clickUpdate(tag);
    this.setState({ redirect: true, clickedDiary: tag })
  }
  render() {
    if (this.state.redirect) {
      // return <Redirect push to={'/ChosenDiary/' + this.state.clickedDiary.key}/>
      return <Redirect push to={{
        pathname: '/ChosenDiary/' + this.state.clickedDiary.key
      }} />
    }
    // console.log(firebase.database().ref('test/').length());

    let newArray = [];
    firebase.database().ref('shared/diaries').on("value", (snapshot) => {
      for (let key in snapshot.val()) {
        newArray.push(snapshot.val()[key])
      }
    })

    let data = newArray.map((diary) => {
      return ({ value: diary.title, count: Math.random() * 20, diary: diary.diary, date: diary.date, 
        key: diary.time, userID: diary.userID, shared:true });
    })
    console.log("sharedDiares in Component");
    console.log(data);
    return (
      <div>
        < div className="container mt-4" id="wordCloud">
          <TagCloud className="text-center" id="tagCloud" minSize={30}
            maxSize={60}
            tags={data}
            onClick={(tag) => this.handleClick(tag)} 
             />
        </div>
      </div >
    );
  }
}

