import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav, button } from 'reactstrap'
import { HashRouter, Route, Switch, Link } from 'react-router-dom';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      diaries: []
    }
  }
  textUpdate = (diary, date, title) => {
    let copiedDiaries = this.state.diaries;
    copiedDiaries.push({ diary, date, title });
    this.setState({ diaries: copiedDiaries });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="text-center" id="title">
            MiliMili
          </h1>
        </header>

        <main className="navBar mt-5">
          <HashRouter>
            <div>
              <Navbar>
                <div className="mx-auto">
                  <Nav>
                    <NavItem><Link to={'/'}>일기쓰기</Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/SavedDiaries'}>나의 일기 </Link>
                    </NavItem>
                    <NavItem className="ml-5"><Link to={'/others'}>남의 일기</Link></NavItem>
                  </Nav>
                </div>
              </Navbar>
              <Switch>
                <Route exact path='/' render={() => <WriteDiary textUpdate={this.textUpdate} />} />
                <Route exact path='/SavedDiaries' render={() => <SavedDiaries diaries={this.state.diaries} />} />
              </Switch>
            </div>
          </HashRouter>
        </main>
      </div>

    );
  }
}

class WriteDiary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textValue: '',
      title: ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleSubmit = (event) => {
    event.preventDefault();
    let date = new Date();
    this.props.textUpdate(this.state.textValue, date.toLocaleString(), this.state.title);
  }
  handleChange = (event) => {
    this.setState({ textValue: event.target.value });
  }

  titleChange = (event) => {
    this.setState({ title: event.target.value })
  }

  render() {
    return (
      <div className="container text-center">
        <form className="myForm mt-5" onSubmit={this.handleSubmit}>
          {/* <div className="text-center"> */}
            <input className="mb-3 p-2" size="71" id= "title" type="text" placeholder="제목" onChange={this.titleChange}></input>
            <div className="form">
              <textarea className="p-2"id="textarea" type="text" rows="20" cols="70" placeholder="오늘하루는 어떠셨나요?" onChange={this.handleChange}></textarea>
            </div>
          {/* </div> */}
            <button type="submit" className="btn btn-warning btn-lg mt-3 mb-3">
              제출
          </button>
        </form>
      </div>);
  }
}

class SavedDiaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }
  render() {
    let diaryLists = this.props.diaries.map((diary) => {
      return <OneSavedDiary key={diary.date} oneDiary={diary} />
    })

    return (
      <div>
        <ul>
          {diaryLists}
        </ul>
      </div>
    );
  }
}

class OneSavedDiary extends Component {
  render() {
    return (
      <li>
        {this.props.oneDiary.title + " " + this.props.oneDiary.diary + " " + this.props.oneDiary.date}
      </li>
    );
  }
}

export default App;
