import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav, button } from 'reactstrap'
import { HashRouter, Route, Switch, Link } from 'react-router-dom';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }
  render() {
    return (
      <div className="App" style={{
        backgroundImage: `url(require("img/camo.jpg"))`,
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        backgroundRepeat: 'no-repeat',
      }}>
        <header className="App-header">
          <h1 className="text-center" id="title">
            MiliMili
          </h1>
        </header>

        <div className="navBar mt-5">
          <HashRouter>
            <Navbar>
              <div className="mx-auto">
                <Nav>
                  <NavItem><Link to={'/'}>일기쓰기</Link>
                  </NavItem>
                  <NavItem><Link to={'/diary'}>나의 일기 </Link>
                  </NavItem>
                  <NavItem className="ml-5"><Link to={'/others'}>남의 일기</Link></NavItem>
                </Nav>
              </div>
            </Navbar>
            <Switch>
              <Route exact path='/' component={WriteDiary}></Route>
            </Switch>
          </HashRouter>
        </div>
      </div>

    );
  }
}

class WriteDiary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textValue: ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }
  render() {

    handleSubmit = (event) => {
      event.preventDefault();
      console.log(this.state.textValue)
    }

    handleChange = (event) => {
      this.setState({ textValue: event.target.value });
    }

    return (
      <form className="myForm mt-5" onSubmit={this.handleSubmit}>
        <div className="diary-control">
          <div className="text-center">
            <div className="form">
              <textarea type="text" rows="20" cols="70" placeholder="오늘하루는 어떠셨나요?" onChange={this.handleChange}></textarea>
            </div>
          </div>
        </div>
        <div className="text-center">
          <button type="submit" className="btn btn-warning btn-lg mt-3 mb-3">
            제출
    </button>
        </div>
      </form>);
  }
}

export default App;
