import React, { Component } from 'react'; //import React Component

import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';



class SignUpForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      'email': undefined,
      'password': undefined,
      // anonymous: false,
      dropdownOpen: false,
      handle: '신분'
    };
    this.toggle = this.toggle.bind(this)
  }

  //update state for specific field
  handleChange(event) {
    let field = event.target.name;
    let value = event.target.value;

    let changes = {};
    changes[field] = value;
    this.setState(changes);
  }

  // signUp button
  handleSignUp(event) {
    event.preventDefault();
    this.props.signUpCallback(this.state.email, this.state.password, this.state.handle);
  }

  //handle signIn button
  handleSignIn(event) {
    event.preventDefault();
    this.props.signInCallback(this.state.email, this.state.password);
  }

  handleType(event) {
    console.log(event.currentTarget.textContent)
    this.setState({ handle: event.currentTarget.textContent});
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  // handleAnonymous(event) {
  //   event.preventDefault();
  //   this.setState({anonymous: true})
  //   this.props.handleAnonymous(this.state.anonymous)
  // }

  render() {
    return (
      <form>
        {/* email field */}
        <div className="form-group">
          <label htmlFor="email">아이디</label>
          <input className="form-control" placeholder="이메일형식" id="email" type="email" name="email" onChange={(e) => this.handleChange(e)} />
        </div>

        {/* password field*/}
        <div className="form-group">
          <label htmlFor="password">비밀번호</label>
          <input className="form-control" id="password" type="password" name="password" onChange={(e) => this.handleChange(e)} />
        </div>

        {/* sign up and sign in */}
        <div className="form-group text-right">
          <button id="signupButton1" className="btn" onClick={(e) => this.handleSignIn(e)}>로그인</button>
          <button id="signupButton" className="btn mr-2" onClick={(e) => this.handleSignUp(e)}>회원가입</button>

          <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} className="d-inline">
            <DropdownToggle caret color="none" >
              {this.state.handle}
        </DropdownToggle>
            <DropdownMenu>
              {/* <DropdownItem header>Header</DropdownItem> */}
              <DropdownItem onClick={(e) => this.handleType(e)}>미필</DropdownItem>
              {/* <DropdownItem disabled>Action (disabled)</DropdownItem>
              <DropdownItem divider /> */}
              <DropdownItem>군필</DropdownItem>
              <DropdownItem>전역</DropdownItem>
              <DropdownItem>others</DropdownItem>
            </DropdownMenu>
          </Dropdown>

          {/* <button className = "btn" onClick={(e) => this.handleAnonymous(e)}>비회원</button> */}
        </div>
      </form>
    )
  }
}

export default SignUpForm
