import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Redirect } from 'react-router-dom';
import { TagCloud } from "react-tagcloud";


class SavedDiaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      clickedDiary: ''
    }
  }

  handleClick(tag) {
    console.log(tag.value)
    this.props.clickUpdate(tag);
    this.setState({ redirect: true, clickedDiary: tag })
  }

  render() {
    // let data = this.props.diaries.map((diary) => {
    //   // return <OneSavedDiary key={diary.date} oneDiary={diary} />
    //   return ({ value: diary.title, count: Math.random() * 30, diary: diary.diary, key: diary.date });
    // })
    let diaryCards = this.props.diaries.map((diary) => {
      return <DiaryCard diary={diary} />
    })

    if (this.state.redirect) {
      // return <Redirect push to={'/ChosenDiary/' + this.state.clickedDiary.key}/>
      return <Redirect push to={{
        pathname: '/ChosenDiary/' + this.state.clickedDiary.key
      }} />

    }

    return (
      <div>
        {/* < div className="container" id="wordCloud"> */}
        {/* <TagCloud className="text-center" id="tagCloud" minSize={20}
              maxSize={50}
              tags={data}
              onClick={(tag) => this.handleClick(tag)} /> */}
          {diaryCards}
        {/* </div > */}
      </div >
    );
  }
}

class DiaryCard extends Component {
  render() {
    return (
      <div>
        <div className=" d-flex width:18rem">
          <div className="card bg-light m-5">
            <div className="card-header">{this.props.diary.date}</div>
            <div className="card-body">
              <h5 className="card-title">{this.props.diary.title}</h5>
              <p className="card-text">{this.props.diary.diary}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}



export default SavedDiaries
