import React, { Component } from 'react'; //import React Component

import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';



class SignUpForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      'email': undefined,
      'password': undefined,
      // anonymous: false,
      dropdownOpen: false,
      handle: undefined,
      modal: false
    };
    this.toggle = this.toggle.bind(this)
    this.modalToggle = this.modalToggle.bind(this);

  }

  //update state for specific field
  handleChange(event) {
    let field = event.target.name;
    let value = event.target.value;

    let changes = {};
    changes[field] = value;
    this.setState(changes);
  }

  // signUp button
  handleSignUp(event) {
    event.preventDefault();
    return (<SignUpModal/>)
    // this.props.signUpCallback(this.state.email, this.state.password, this.state.handle);
  }

  //handle signIn button
  handleSignIn(event) {
    event.preventDefault();
    this.props.signInCallback(this.state.email, this.state.password);
  }

  handleType(event) {
    console.log(event.currentTarget.textContent)
    this.setState({ handle: event.currentTarget.textContent});
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  modalToggle() {
    this.setState({
      modal: !this.state.modal
    });
  }

  buttonChange(event) {
    this.setState({btnName: "hi"})
  }

  // handleAnonymous(event) {
  //   event.preventDefault();
  //   this.setState({anonymous: true})
  //   this.props.handleAnonymous(this.state.anonymous)
  // }

  render() {
    return (
      <form>
        {/* email field */}
        <div className="form-group">
          <label htmlFor="email">아이디</label>
          <input className="form-control" placeholder="이메일형식" id="email" type="email" name="email" onChange={(e) => this.handleChange(e)} />
        </div>

        {/* password field*/}
        <div className="form-group">
          <label htmlFor="password">비밀번호</label>
          <input className="form-control" id="password" type="password" name="password" onChange={(e) => this.handleChange(e)} />
        </div>

        {/* sign up and sign in */}
        <div className="form-group text-right">
          <button id="signupButton1" className="btn" onClick={(e) => this.handleSignIn(e)}>로그인</button>
          <button id="signupButton" className="btn mr-2" onClick={this.modalToggle}>회원가입</button>

          <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} className="d-inline" 
          onChange={this.buttonChange}>
            <DropdownToggle caret color="none" >
              {this.state.handle}
        </DropdownToggle>
            <DropdownMenu>
              {/* <DropdownItem header>Header</DropdownItem> */}
              <DropdownItem onClick={(e) => this.handleType(e)}>미필</DropdownItem>
              {/* <DropdownItem disabled>Action (disabled)</DropdownItem>
              <DropdownItem divider /> */}
              <DropdownItem onClick={(e) => this.handleType(e)}>군필</DropdownItem>
              <DropdownItem onClick={(e) => this.handleType(e)}>전역</DropdownItem>
              <DropdownItem onClick={(e) => this.handleType(e)}>others</DropdownItem>
            </DropdownMenu>
          </Dropdown>

          <Modal isOpen={this.state.modal} toggle={this.modalToggle}>
          <ModalHeader toggle={this.modalToggle}>Modal title</ModalHeader>
          <ModalBody>
          <form>
        {/* email field */}
        <div className="form-group">
          <label htmlFor="email">아이디</label>
          <input className="form-control" placeholder="이메일형식" id="email" type="email" name="email" onChange={(e) => this.handleChange(e)} />
        </div>

        {/* password field*/}
        <div className="form-group">
          <label htmlFor="password">비밀번호</label>
          <input className="form-control" id="password" type="password" name="password" onChange={(e) => this.handleChange(e)} />
        </div>
        <div className="form-group text-right">
          <button id="signupButton1" className="btn" onClick={(e) => this.handleSignUp(e)}>회원가입</button>
          </div>
        </form>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.modalToggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.modalToggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
          {/* <button className = "btn" onClick={(e) => this.handleAnonymous(e)}>비회원</button> */}
        </div>
      </form>
    )
  }
}

class SignUpModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState({
      modal: !this.state.modal
    });
  }

  render() {
    return (
      <div>
        {/* <Button color="danger" onClick={this.toggle}>{this.props.buttonLabel}</Button> */}
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default SignUpForm
