import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, Nav } from 'reactstrap'
import { HashRouter, Route, Switch, Link, Redirect } from 'react-router-dom';
import { TagCloud } from "react-tagcloud";
import _ from 'lodash';
import firebase from 'firebase/app';
import 'firebase/database';
import SignUpForm from './SignUpForm';
import ChosenDiary from './ChosenDiary'
import SavedDiaries from './SavedDiaries'
import WriteDiary from './WriteDiary'
// import { forEach } from '@firebase/util';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      diaries: [],
      sharedDiares: [],
      clickedDiary: '',
      clickedLogIn: false,
    }
  }
  textUpdate = (diary, date, title) => {
    // let copiedDiaries = [];
    // let copiedDiaries = this.state.diaries;
    console.log(this.state.user)
    let currentUser = this.state.user;
    let newData = {
      userID: currentUser.uid,
      time: firebase.database.ServerValue.TIMESTAMP, diary: diary,
      title: title, date: date
    }
    // copiedDiaries.push(newData);
    let diariesRef = firebase.database().ref(`diaries/${currentUser.uid}`)
    console.log(newData);
    diariesRef.push(newData);

    // diariesRef.on("value", (snapshot) => {
    //   this.setState({diaries: snapshot.val()})
    // })
    // firebase.database().ref(`test/${currentUser.uid}/diaries`).child(date).set(newData)

    // console.log(firebase.database().ref(`text/${currentUser.uid}/diaries`).child(date))
    // this.setState({ diaries: copiedDiaries });
  }

  shareDiary = (diary) => {
    console.log(diary)
    // let currentUser = this.state.user;
    // let data = {
    //   userID: currentUser.uid,
    //   time: firebase.database.ServerValue.TIMESTAMP, diary: diary.diary,
    //   title: diary.value, date: diary.key
    // }
    let shared = false
    firebase.database().ref('shared/diaries').on("value", (snapshot) => {
      // for (let key in snapshot.val()) {
      //   if(snapshot.val()[key].date === diary.key && snapshot.val()[key].userID === diary.userID){
      //     shared = true;
      //   }
      // }
      //if shared diary exists already
      for (let key in snapshot.val()) {
      if (snapshot.val()[diary.id] === snapshot.val()[key]) {
        shared = true;
      }
    }
    })

    if (shared === false) {
      // firebase.database().ref(`shared/diaries/`).child(diary.userID +'-'+diary.key).set(data)
      firebase.database().ref(`shared/diaries/`).child(diary.id).set(diary)

    } else {
      alert("이미 공유하신 게시물입니다!")
    }
  }

  removeMySharedDiary = (id) => {
    console.log("sharedremove:")
    console.log(id)
    // console.log(date)
    // firebase.database().ref(`shared/diaries`).on("value", (snapshot) => {
    //   for(let key in snapshot.val()){
    //     if(snapshot.val()[key].date === date && snapshot.val()[key].userID === userID){
    //       console.log("key:")
    //       console.log(key);
    //       firebase.database().ref(`shared/diaries`).child(key).remove()
    //     } 
    //   }
    // })

    firebase.database().ref(`shared/diaries`).child(id).remove()
  }

  removeDiary = (id) => {
    // let copiedDiaries = this.state.diaries;
    // _.remove(copiedDiaries, (diary) => {
    //   return diary.id === id
    // })

    let diariesRef = firebase.database().ref(`diaries/${this.state.user.uid}`)
    diariesRef.child(id).remove();

    // diariesRef.child(id).on("value", (snapshot) => {
    //   this.setState({diaries: snapshot.val()})
    // })
    // console.log(copiedDiaries)
    // this.setState({ diaries: copiedDiaries })
  }

  clickUpdate = (diary) => {
    // console.log(diary.value)
    this.setState({ clickedDiary: diary })
    // console.log(this.state.clickedDiary.value)
  }
  clickLogIn = () => {
    this.setState({ clickedLogIn: true })
  }

  componentDidMount() {
    this.authUnRegFunc = firebase.auth().onAuthStateChanged((firebaseUser) => {
      if (firebaseUser) {
        this.setState({
          user: firebaseUser,
        })
        //setting state for my private diaries
        firebase.database().ref(`diaries/${this.state.user.uid}`).on("value", (snapshot) => {
          console.log("posted");
          console.log(snapshot.val())
          // let newArray = [];
          // for (let key in snapshot.val()) {
          //   newArray.push(snapshot.val()[key])
          // }
          // console.log("snapshot:")
          // console.log(newArray)
          // this.setState({ diaries: newArray })
          let diariesObject = [];
          if (snapshot.exists()) {
            let diariesKeys = Object.keys(snapshot.val())
            // console.log(chirpsKeys);
            console.log(snapshot.val())
            diariesObject = diariesKeys.map((key) => {
              let diariesObj = snapshot.val()[key];
              console.log(key)
              diariesObj.id = key;
              return diariesObj;
            })
          }

          this.setState({ diaries: diariesObject })
        })
        //setting state for my shared diaries


        firebase.database().ref(`shared/diaries`).on("value", (snapshot) => {
          let sharedObject = [];
          if (snapshot.exists()) {
            let sharedKeys = Object.keys(snapshot.val())
            // console.log(chirpsKeys);
            sharedObject = sharedKeys.map((key) => {
              let sharedObj = snapshot.val()[key];
              sharedObj.id = key;
              return sharedObj;
            })
            this.setState({ sharedDiares: sharedObject })
          }
        })
      } else {
        this.setState({ user: null })
      }
    })
